INSERT INTO `contents` VALUES ('6209', '3', '20', '299', 'Encienda y apague la bombilla cinco veces seguidas (2 segundos encendida y 2 segundos apagada), tras esto, la bombilla comenzará a parpadear. Después de 5 segundos, se reiniciará y mostrará el color blanco, lo que significa que la bombilla se ha restablecido a su valor predeterminado', '0', null, null);
INSERT INTO `contents` VALUES ('6210', '3', '20', '300', 'Encienda y apague la bombilla cinco veces seguidas (2 segundos encendida y 2 segundos apagada), tras esto, la bombilla comenzará a parpadear. Después de 5 segundos, se reiniciará y mostrará el color blanco, lo que significa que la bombilla se ha restablecido a su valor predeterminado.', '0', null, null);
INSERT INTO `contents` VALUES ('6211', '3', '20', '301', 'Presione y mantenga presionado el botón RESET en el panel inferior con un alfiler durante aproximadamente 5 segundos, suelte el botón RESET después de que la luz empiece a parpadear. Si puede ver el parpadeo de la luz durante 5 segundos y luego apagarse, significa que se ha reiniciado correctamente.', '0', null, null);
INSERT INTO `contents` VALUES ('6212', '3', '20', '302', 'Encienda y apague la luz del techo cinco veces seguidas (2 segundos encendida y 2 segundos apagada), tras esto, la luz de techo comenzará a parpadear. Después de 5 segundos, se reiniciará automáticamente y se encenderá la luz blanca, lo que significa que la luz de techo se ha restablecido a su valor predeterminado.', '0', null, null);
