INSERT INTO `contents` VALUES ('4896', '3', '18', '299', 'Schalten Sie die Lampe fünfmal schnell hintereinander ein und aus (2 Sekunden ein und 2 Sekunden aus), dann beginnt die Lampe zu blinken. Nach 5 Sekunden wird sie neu gestartet und zeigt eine weiße Farbe an, was bedeutet, dass die Lampe auf die Standardeinstellung zurückgesetzt wurde.', '0', null, '2017-12-27 19:08:19');
INSERT INTO `contents` VALUES ('4897', '3', '18', '300', 'Schalten Sie die Lampe fünfmal schnell hintereinander ein und aus (2 Sekunden ein und 2 Sekunden aus), dann beginnt die Lampe zu blinken. Nach 5 Sekunden wird sie neu gestartet und zeigt eine weiße Farbe an, was bedeutet, dass die Lampe auf die Standardeinstellung zurückgesetzt wurde.', '0', null, '2017-12-27 19:08:54');
