INSERT INTO `contents` VALUES ('5554', '3', '19', '301', 'Нажмите и удерживайте кнопку RESET на нижней панели с помощью шпильки в течение около 5 секунд, затем отпустите кнопку RESET после того, как свет начнет мигать. Если вы видете вспышку света в течение 5 секунд, а затем она исчезает, это означает, что перезагрузка прошла успешно.', '0', null, null);
