INSERT INTO `contents` VALUES ('5556', '3', '19', '303', ' Одновременно нажмите и удерживайте кнопку «Вкл. /Выкл.» и кнопку «Режим» в течение 5 секунд, после чего свет начнет мерцать. Через 3 секунды прикроватная лампа перезапустится автоматически и включит белый свет, что означает, что потолочное освещение было перенастроено согласно значениям по умолчанию.', '0', null, null);
