INSERT INTO `contents` VALUES ('8836', '3', '24', '299', 'Schakel de lamp vijf keer achter elkaar in en uit (2 seconden aan en 2 seconden uit). Ter bevestiging gaat de lamp knipperen. Na 5 seconden wordt deze opnieuw gestart en geeft de lamp wit licht. Dit betekent dat de lamp opnieuw is ingesteld op de standaardwaarde.', '0', null, null);
INSERT INTO `contents` VALUES ('8837', '3', '24', '300', 'Schakel de lamp vijf keer achter elkaar in en uit (2 seconden aan en 2 seconden uit). Ter bevestiging gaat de lamp knipperen. Na 5 seconden wordt deze opnieuw gestart en geeft de lamp wit licht. Dit betekent dat de lamp opnieuw is ingesteld op de standaardwaarde.', '0', null, null);
INSERT INTO `contents` VALUES ('8838', '3', '24', '301', 'Druk rechtsreeks op de RESET-knop aan de onderkant en houd deze ongeveer 5 seconden ingedrukt met een pin, laat de RESET-knop los nadat het licht begint te knipperen. Als u het lampje gedurende 5 seconden ziet knipperen en vervolgens ziet uitgaan, betekent dit dat het lampje met succes is gereset.', '0', null, null);
INSERT INTO `contents` VALUES ('8839', '3', '24', '302', 'Schakel de plafondlamp vijf keer achter elkaar in en uit (2 seconden aan en 2 seconden uit). De plafondlamp zal beginnen ademen. Na 5 seconden wordt de lamp automatisch opnieuw opgestart en wordt wit licht ingeschakeld, wat betekent dat de plafondlamp is gereset naar de standaardinstelling.', '0', null, null);
