INSERT INTO `contents` VALUES ('11325', '15', '104', '1044', 'The rated input in China and Europe is 220V-240V~50/60Hz 0.05A. The rated input in the US is 120V~60Hz 0.09A. The rated input in other regions is 100V-240V~50/60Hz 0.09A.', '0', '2018-04-28 19:09:38', '2018-04-28 19:11:33');
INSERT INTO `contents` VALUES ('11326', '15', '104', '1045', 'What should pay attention to in the installation and usage of the bulb?', '0', '2018-04-28 19:10:51', '2018-04-28 19:12:09');
